#include <bits/stdc++.h>
using namespace std;

class Solution
{
public:
    bool hasAllCodes(string s, int k)
    {
        unordered_set<string> ms;
        int n = s.size();
        for(int i = 0; i < n - k + 1; ++i)
        {
            ms.insert(s.substr(i, k));
        }
        // int range = 1 << k;
        // for(int i = 0; i < range; ++i)
        // {
        //     bitset<20> t(i);
        //     if(ms.find(t.to_string().substr(20-k, k)) == ms.end())
        //     	return false;
        //     // int idx = s.find(t.to_string().substr(32-k, k));
        //     // if(idx == string::npos)
        //     //     return false;
        // }
        // return true;
        return ms.size() == (1 << k);
    }
};

int main(int argc, char* argv[])
{
    Solution soluter;
    cout << soluter.hasAllCodes("", 7) << endl;
    return 0;
}